resources.curl:
curl -X PUT -H "Authorization: Bearer <YOUR ACCESS TOKEN>" -H "X-ADN-Pretty-JSON: 1" \
    -H "Content-Type: application/json" -d "{
  \"name\": \"@adnapi has a new name\",
  \"locale\": \"en\",
  \"timezone\": \"US/Central\",
  \"description\": {
    \"text\": \"new description\"
  },
  \"annotations\": [
    {
      \"type\": \"net.app.core.directory.blog\",
      \"value\": {
        \"url\": \"http://mynewblog.com\"
      }
    }
  ]
}" \
    "https://api.app.net/users/me?include_user_annotations=1"

curl -X PATCH -H "Authorization: Bearer <YOUR ACCESS TOKEN>" -H "X-ADN-Pretty-JSON: 1" \
    -H "Content-Type: application/json" -d "{
  \"name\": \"Mark Thurman 3\"
}" \
    "https://api.app.net/users/me"

curl -X POST -H "Authorization: Bearer <YOUR ACCESS TOKEN>" -H "X-ADN-Pretty-JSON: 1" \
    -H "Content-Type: application/json" -d "{
  \"type\": \"com.example.channel\",
  \"writers\": {
    \"user_ids\": [
      \"@berg\",
      \"1\"
    ]
  }
}" \
    "https://api.app.net/channels"

curl -X PUT -H "Authorization: Bearer <YOUR ACCESS TOKEN>" -H "X-ADN-Pretty-JSON: 1" \
    -H "Content-Type: application/json" -d "{
  \"readers\": {
    \"public\": true
  }
}" \
    "https://api.app.net/channels/1"

curl -X POST -H "Authorization: Bearer <YOUR ACCESS TOKEN>" -H "X-ADN-Pretty-JSON: 1" \
    -H "Content-Type: application/json" -d "{
  \"text\": \"Hello channel!\"
}" \
    "https://api.app.net/channels/1/messages"

echo '{
  "type": "com.example.test",
  "annotations": [
    {
      "type": "com.example.annotation",
      "value": {
        "foo": "bar"
      }
    }
  ]
}' | \
    curl -X POST -H "Authorization: Bearer <YOUR ACCESS TOKEN>" -H "X-ADN-Pretty-JSON: 1" \
    -F "metadata=@-;type=application/json" -F "content=@filename.jpg" \
    "https://api.app.net/files"

curl -X PUT -H "Authorization: Bearer <YOUR ACCESS TOKEN>" -H "X-ADN-Pretty-JSON: 1" \
    -H "Content-Type: application/json" -d "{
  \"name\": \"update_filename.jpg\"
}" \
    "https://api.app.net/files/1"

curl -X POST -H "Authorization: Bearer <YOUR ACCESS TOKEN>" -H "X-ADN-Pretty-JSON: 1" \
    -H "Content-Type: application/json" -d "{
  \"clauses\": [
    {
      \"field\": \"/data/entities/hashtags/*/name\",
      \"object_type\": \"post\",
      \"operator\": \"matches\",
      \"value\": \"rollout\"
    }
  ],
  \"match_policy\": \"include_any\",
  \"name\": \"Posts about rollouts\"
}" \
    "https://api.app.net/filters"

curl -X PUT -H "Authorization: Bearer <YOUR ACCESS TOKEN>" -H "X-ADN-Pretty-JSON: 1" \
    -H "Content-Type: application/json" -d "{
  \"match_policy\": \"include_any\",
  \"clauses\": [
    {
      \"operator\": \"matches\",
      \"field\": \"/data/entities/hashtags/*/name\",
      \"object_type\": \"post\",
      \"value\": \"rollout\"
    },
    {
      \"operator\": \"matches\",
      \"field\": \"/data/entities/hashtags/*/name\",
      \"object_type\": \"post\",
      \"value\": \"bug\"
    }
  ],
  \"name\": \"Posts about rollouts or bugs\"
}" \
    "https://api.app.net/filters/1"

curl -X POST -H "Authorization: Bearer <YOUR ACCESS TOKEN>" -H "X-ADN-Pretty-JSON: 1" \
    -H "Content-Type: application/json" -d "{
  \"text\": \"This is #awesome tell @voidfiles about http://google.com\"
}" \
    "https://api.app.net/text/process"
